import React, { Component } from 'react';
import { connect } from 'react-redux';
import { addToCart, addToCompare } from '../../store/actions/cartActions';
import Link from 'next/link';
import ReactTooltip from 'react-tooltip'
import { ToastContainer, toast, Slide } from 'react-toastify';
import QuickView from '../Modal/QuickView';

class CategoryProducts extends Component {
    state = {
        modalOpen: false,
        modalImage: '',
        price: 0,
        idd: null
    };

    render() {
        const CategoryProducts = props => (
            <section className="best-sellers-area pb-60">
                <ReactTooltip  />
                <ToastContainer transition={Slide} />
                <div className="container">
                    <div className="section-title">
                        <h2><span className="dot"></span> All Brands</h2>
                    </div>

                    <div className="row">
                        {props.brands.map(brand => (
                            <div className="col-lg-3 col-md-6 col-sm-6 col-6" key={brand.id}>
                                <div className="single-product-box">
                                    <div className="product-image">
                                        <Link href={`/brands/?id=${brand.id}`}>
                                            <a>
                                                <img src={brand.image} alt="image" />
                                                <img src={brand.imageHover} alt="image" />
                                            </a>
                                        </Link>
                                    </div>

                                    <div className="product-content">
                                        <h3>
                                            <Link href={`/brands/?id=${brand.id}`}>
                                                <a>{brand.title}</a>
                                            </Link>
                                        </h3>
                                    </div>
                                </div>
                            </div>
                        ))}

                    </div>
                </div>
            </section>
        )
    }
}

CategoryProducts.getInitialProps = async function() {
    const res = await fetch('https://api.tvmaze.com/search/shows?q=batman');
    const data = await res.json();
  
    console.log(`Show data fetched. Count: ${data.length}`);
  
    return {
      brands: data.map(entry => entry.brand)
    };
};
  

export default CategoryProducts;
