import React, { Component } from 'react';
import { connect } from 'react-redux';
import { addToCart, addToCompare } from '../../store/actions/cartActions';
import Link from 'next/link';
import ReactTooltip from 'react-tooltip'
import { ToastContainer, toast, Slide } from 'react-toastify';
import QuickView from '../Modal/QuickView';

class CategoryProducts extends Component {
    state = {
        modalOpen: false,
        modalImage: '',
        price: 0,
        idd: null
    };
    static async getInitialProps() {
        const res = await fetch('CategoryProducts.json')
        const brands = await res.json()
        return { brands }
    }
    componentWillMount() {
        this.setState({
          brands: this.props.brands
        })
      }

    render() {
        return (
            <section className="best-sellers-area pb-60">
                <ReactTooltip  />
                <ToastContainer transition={Slide} />
                <div className="container">
                    <div className="section-title">
                        <h2><span className="dot"></span> All Brands</h2>
                    </div>

                    <div className="row">
                        {this.state.brands.map((data, idx) => (
                            <div className="col-lg-3 col-md-6 col-sm-6 col-6" key={idx}>
                                <div className="single-product-box">
                                    <div className="product-image">
                                        <Link href={`/brands/?id=${data.id}`}>
                                            <a>
                                                <img src={data.image} alt="image" />
                                                <img src={data.imageHover} alt="image" />
                                            </a>
                                        </Link>
                                    </div>

                                    <div className="product-content">
                                        <h3>
                                            <Link href={`/brands/?id=${data.id}`}>
                                                <a>{data.title}</a>
                                            </Link>
                                        </h3>
                                    </div>
                                </div>
                            </div>
                        ))}

                    </div>
                </div>
            </section>
        );
    }
}



export default CategoryProducts;
